module.exports = (o, t) => {
    if(t === "array") {
      if(!Array.isArray(o)) {
        throw new TypeError(`Expected a(n) ${t}, but instead got ${typeof o}.`)
      }
    } else {
      if(typeof o !== t) {
        throw new TypeError(`Expected a(n) ${t}, but instead got ${typeof o}.`)
      }
    }
  }