const library = require('../')
const paheal = new library()

async function check() {
    // Start to search some media
    let search = await paheal.search('devil')
    // console.log(search.results)

    // or

    let media = await paheal.search('devil').then(search => search.results)
    console.log(media);

    // Find media by author 
    let search = await paheal.byAuthor('noname')
    console.log(search.results)

    // Next Page
    let newPage = search.next()
    console.log(newPage.results);

    // Previous Page
    let prevPage = search.prev()
    console.log(prevPage.results);

    // Last Page
    let lastPage = search.last()
    console.log(lastPage.results);

    // First Page
    let firstPage = search.first()
    console.log(firstPage.results);

    // Get Page
    let selectedPage = search.getPage(2)
    console.log(selectedPage.results);

    // Check for all errors in a library without catch(i stop using reject, when after 15^ node.js, Promise use default strict mode for reject)
    if (search.status) return search.message;
}
check()